package foreach;

public class forEach {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float arr[] = {1, 2, 3, 4, 5, 6};
		
		// For each loop. It will go through every element of the array
		for (float e: arr) {
			System.out.println(e); // The array is named e in the for.
		}
	}

}
