package numbers;

public class Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// These are some methods. There are more, check http://tutorials.jenkov.com/java/math-operators-and-math-class.html
		
		float i = 59.6f;
		int a = 100;
		
		// Round number.
		System.out.println(Math.round(i));
		
		// Logarithm.
		System.out.println(Math.E);
		System.out.println(Math.log(a)); // Base e.
		
		// Power.
		System.out.println(Math.pow(10, 2)); // Power 10^2.
		
		// Square root.
		System.out.println(Math.sqrt(4));
		
		// Sin trigonometry.
		double pi = Math.PI;
		System.out.println(Math.sin(pi));
		
	}

}
